import React, { useState, useEffect } from 'react';

const MarketOverview = () => {
  const [marketData, setMarketData] = useState({
    indices: [],
    currencies: [],
    commodities: [],
    loading: true,
    error: null
  });

  // Major market indices to track
  const indices = [
    { symbol: '^GSPC', name: 'S&P 500' },
    { symbol: '^DJI', name: 'Dow Jones' },
    { symbol: '^IXIC', name: 'NASDAQ' },
    { symbol: '^FTSE', name: 'FTSE 100' },
    { symbol: '^N225', name: 'Nikkei 225' }
  ];

  // Major currencies
  const currencies = [
    { symbol: 'EUR=X', name: 'EUR/USD' },
    { symbol: 'JPY=X', name: 'USD/JPY' },
    { symbol: 'GBP=X', name: 'GBP/USD' },
    { symbol: 'CNY=X', name: 'USD/CNY' }
  ];

  // Major commodities
  const commodities = [
    { symbol: 'GC=F', name: 'Gold' },
    { symbol: 'SI=F', name: 'Silver' },
    { symbol: 'CL=F', name: 'Crude Oil' },
    { symbol: 'BTC-USD', name: 'Bitcoin' }
  ];

  const formatPercentage = (value) => {
    const num = parseFloat(value);
    const sign = num > 0 ? '+' : '';
    return `${sign}${num.toFixed(2)}%`;
  };

  const getColorClass = (value) => {
    const num = parseFloat(value);
    return num > 0 ? 'text-green-600 dark:text-green-500' : 
           num < 0 ? 'text-red-600 dark:text-red-500' : 
           'text-gray-600 dark:text-gray-400';
  };

  useEffect(() => {
    const fetchMarketData = async () => {
      try {
        setMarketData(prev => ({ ...prev, loading: true, error: null }));

        // In a real app, you would fetch from a proper financial API
        // This is simulated data for demonstration
        const mockData = {
          indices: indices.map(index => ({
            ...index,
            price: Math.round(Math.random() * 30000 + 10000),
            change: (Math.random() * 4 - 2).toFixed(2)
          })),
          currencies: currencies.map(currency => ({
            ...currency,
            price: (Math.random() * 2 + 0.5).toFixed(4),
            change: (Math.random() * 2 - 1).toFixed(2)
          })),
          commodities: commodities.map(commodity => ({
            ...commodity,
            price: Math.round(Math.random() * 2000 + 500),
            change: (Math.random() * 4 - 2).toFixed(2)
          }))
        };

        setMarketData(prev => ({
          ...prev,
          ...mockData,
          loading: false
        }));
      } catch (error) {
        setMarketData(prev => ({
          ...prev,
          loading: false,
          error: 'Failed to fetch market data'
        }));
      }
    };

    fetchMarketData();
    const interval = setInterval(fetchMarketData, 60000); // Update every minute
    return () => clearInterval(interval);
  }, [indices, currencies, commodities]);

  const renderMarketItem = (item) => (
    <div key={item.symbol} className="flex justify-between items-center p-2 rounded-md bg-gray-50 dark:bg-gray-700">
      <div className="flex flex-col">
        <span className="text-sm font-medium text-gray-900 dark:text-gray-100">{item.name}</span>
        <span className="text-xs text-gray-500 dark:text-gray-400">{item.symbol}</span>
      </div>
      <div className="flex flex-col items-end">
        <span className="text-sm font-medium text-gray-900 dark:text-gray-100">
          {typeof item.price === 'number' ? item.price.toLocaleString() : item.price}
        </span>
        <span className={`text-xs font-medium ${getColorClass(item.change)}`}>
          {formatPercentage(item.change)}
        </span>
      </div>
    </div>
  );

  const renderMarketSection = (title, items, isLoading) => (
    <div className="bg-white dark:bg-gray-800 p-4 rounded-lg shadow-sm">
      <h3 className="text-lg font-semibold mb-3 text-gray-900 dark:text-gray-100">{title}</h3>
      <div className="grid grid-cols-1 gap-2">
        {isLoading ? (
          <div className="animate-pulse space-y-2">
            {[1, 2, 3].map(i => (
              <div key={i} className="h-12 bg-gray-200 dark:bg-gray-700 rounded"></div>
            ))}
          </div>
        ) : (
          items.map(renderMarketItem)
        )}
      </div>
    </div>
  );

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
      {renderMarketSection('Market Indices', marketData.indices, marketData.loading)}
      {renderMarketSection('Currencies', marketData.currencies, marketData.loading)}
      {renderMarketSection('Commodities & Crypto', marketData.commodities, marketData.loading)}
      
      {marketData.error && (
        <div className="col-span-full bg-red-50 dark:bg-red-900 p-4 rounded-lg">
          <p className="text-red-600 dark:text-red-200">{marketData.error}</p>
        </div>
      )}
    </div>
  );
};

export default MarketOverview;
